<?phpnamespace app\common\model;use think\db;use think\Model;class SysConfig extends Model{	public $mc_data = [];	public $err = "";	public function init1()	{	     		if ($this->mc_data) {			return true;		}		$data = mc('msphp_sys_config');		if ($data) {			$this->mc_data = $data;			return true;		}		$config = db::table('sys_config')->select();		if (!$config) {			return false;		}		foreach ($config as $k => $v) {			$data[$v['name']] = $v['value'];			if ($v['type'] == 2) {				if (strstr($v['value'], "|")) {					$data[$v['name']] = explode('|', $v['value']);				} else {					$data[$v['name']] = [$v['value']];				}			}		}		     		mc('msphp_sys_config', $data);		$this->mc_data = $data;		return true;	}	public function get1($name = null)	{		$this->init();		return isset($this->mc_data[$name]) ? $this->mc_data[$name] : "";	}	public function setValueOrName($name = '', $value = '')	{		return db::table('sys_config')->where(['name' => $name])->save(['value' => $value]);	}	public function addData($data = [])	{		return db::table('sys_config')->add($data);	}	public function lists()	{		$this->init1();		return $this->mc_data ? $this->mc_data : [];	}}